import{_ as a,v as t,b as e,R as r}from"./chunks/framework.cc13f38d.js";const b=JSON.parse('{"title":"利润表","description":"","frontmatter":{},"headers":[],"relativePath":"金融/基础知识/财务报表/初始笔记(非整理)/03 利润表/01 利润表.md","filePath":"金融/基础知识/财务报表/初始笔记(非整理)/03 利润表/01 利润表.md","lastUpdated":1688291041000}'),l={name:"金融/基础知识/财务报表/初始笔记(非整理)/03 利润表/01 利润表.md"},i=r('<h1 id="利润表" tabindex="-1">利润表 <a class="header-anchor" href="#利润表" aria-label="Permalink to &quot;利润表&quot;">​</a></h1><p>利润表又叫损益表，反映企业的盈利能力</p><p>每股收益</p><ul><li>基本每股收益：按照现有股本计算每股收益</li><li>稀释每股收益：按照现有股本加上潜在股本计算每股收益(潜在股本如认证股权、股票期权、可转债等)</li></ul><h2 id="合并利润表与母公司利润表" tabindex="-1">合并利润表与母公司利润表 <a class="header-anchor" href="#合并利润表与母公司利润表" aria-label="Permalink to &quot;合并利润表与母公司利润表&quot;">​</a></h2><p>母公司利润表：母公司及控股子公司 <br> 合并利润表：母公司和子公司形成的集团整体，抵消了内部交易 <br> 简单理解为：合并 = 母公司 + 少数股东 <br></p><p>除此之外还有一些不计入净利润但直接计入资本公积的利得或损失，列入 其他综合收益 <br></p><p>投资收益明细</p><ul><li>成本法核算的长期股权投资收益</li><li>持有至到期投资持有期间的投资收益</li></ul><p><strong>净利润</strong>关注<strong>归属于母公司所有者的净利润</strong>科目即可</p><h2 id="净利润产生过程" tabindex="-1">净利润产生过程 <a class="header-anchor" href="#净利润产生过程" aria-label="Permalink to &quot;净利润产生过程&quot;">​</a></h2><p>净利润的产生过程分为</p><ol><li>从营业收入到营业利润的过程</li><li>从营业利润到净利润的过程</li></ol>',13),o=[i];function s(n,p,d,h,_,c){return t(),e("div",null,o)}const f=a(l,[["render",s]]);export{b as __pageData,f as default};
